      const sudachiData = document.querySelector('sudachi');
      const letterPaper = document.getElementById('letterPaper');
      const sudachiLogo = document.getElementById('sudachiLogo');
      const letterContent = document.getElementById('letterContent');

      // ç¡®ä¿é¡µé¢åŠ è½½æ—¶æ»šåŠ¨åˆ°åˆé€‚ä½ç½®
      setTimeout(() => {
        window.scrollTo(0, 0);
      }, 100);

      // è§£æå¹¶æ˜¾ç¤ºsudachiæ ‡ç­¾ä¸­çš„å†…å®¹
      function parseAndDisplayContent() {
        if (sudachiData && letterContent) {
          const content = sudachiData.textContent.trim();
          letterContent.textContent = content;
        }
      }

      // åˆ‡æ¢ä¿¡çº¸å±•å¼€/æ”¶èµ·çŠ¶æ€
      function toggleLetterState() {
        // ä¿å­˜å½“å‰æ»šåŠ¨ä½ç½®
        const scrollTop = window.pageYOffset || document.documentElement.scrollTop;

        // åˆ‡æ¢collapsedç±»
        letterPaper.classList.toggle('collapsed');

        // å¦‚æœæ˜¯å±•å¼€çŠ¶æ€ï¼Œç¡®ä¿å†…å®¹å·²è§£æ
        if (!letterPaper.classList.contains('collapsed')) {
          parseAndDisplayContent();
        }

        // é˜²æ­¢é¡µé¢è·³åŠ¨ï¼Œå»¶è¿Ÿæ¢å¤æ»šåŠ¨ä½ç½®
        setTimeout(() => {
          // å±•å¼€åç¡®ä¿é¡µé¢é¡¶éƒ¨å¯¹é½
          if (!letterPaper.classList.contains('collapsed')) {
            window.scrollTo(0, 0);
          } else {
            // æ”¶èµ·åå±…ä¸­æ˜¾ç¤ºæŒ‰é’®
            const buttonRect = letterPaper.getBoundingClientRect();
            const viewportHeight = window.innerHeight;
            const targetScroll = window.pageYOffset + buttonRect.top - (viewportHeight / 2 - buttonRect.height / 2);
            window.scrollTo(0, Math.max(0, targetScroll));
          }
        }, 50);
      }

      // ä¸ºLogoæ·»åŠ ç‚¹å‡»äº‹ä»¶ç›‘å¬å™¨
      sudachiLogo.addEventListener('click', function(e) {
        e.stopPropagation();
        toggleLetterState();
      });

      // åœ¨æ”¶èµ·çŠ¶æ€ä¸‹ï¼Œæ•´ä¸ªä¿¡çº¸éƒ½å¯ä»¥ç‚¹å‡»
      letterPaper.addEventListener('click', function(e) {
        if (letterPaper.classList.contains('collapsed')) {
          e.stopPropagation();
          toggleLetterState();
        }
      });

      // é˜²æ­¢åœ¨å±•å¼€çŠ¶æ€ä¸‹ç‚¹å‡»å†…å®¹åŒºåŸŸè§¦å‘æ”¶èµ·
      letterContent.addEventListener('click', function(e) {
        e.stopPropagation();
      });

      // åˆå§‹åŒ–é¡µé¢æ—¶ç¡®ä¿æ˜¯æ”¶èµ·çŠ¶æ€
      letterPaper.classList.add('collapsed');
    });
  </script>
