  <script>
    // --- Utility Functions ---
    const parseResources = (elementId, tag) => {
      const resources = {};
      const rawData = document.getElementById(elementId)?.textContent;
      if (!rawData) return resources;
      const match = rawData.match(new RegExp(`<${tag}>([\\s\\S]*)<\\/${tag}>`));
      if (!match) return resources;
      match[1].trim().split('\n').filter(line => line.trim()).forEach(line => {
        const [name, ...urlParts] = line.split('|');
        const url = urlParts.join('|');
        if (name && url) resources[name.trim()] = url.trim();
      });
      return resources;
    };

    const parseMessagesAndOptions = (elementId, tag) => {
        const messages = [];
        const rawData = document.getElementById(elementId)?.textContent;
        if (!rawData) return messages;
        const match = rawData.match(new RegExp(`<${tag}>([\\s\\S]*)<\\/${tag}>`));
        if (!match) return messages;

        const lines = match[1].trim().split('\n').filter(line => line.trim());
        let currentMessage = null;

        lines.forEach(line => {
            if (line.startsWith('Event|')) {
                const parts = line.split('|');
                if (parts.length >= 3) {
                    messages.push({
                        type: 'event',
                        name: 'Event',
                        content: parts[1].trim() || "æ— å†…å®¹",
                        background: parts[2].trim() || "æ— "
                    });
                }
            }
            else if (line.includes('|') && !line.startsWith('[')) {
                const parts = line.split('|');
                const [name, content, background, music, sfx, transition] = parts.map(p => (p || "").trim() || "æ— ");
                currentMessage = {
                    type: 'dialog',
                    name,
                    content,
                    background,
                    music,
                    sfx,
                    transition,
                    options: []
                };
                messages.push(currentMessage);
            }
            else if (line.startsWith('[') && currentMessage && currentMessage.type === 'dialog') {
                const optionText = line.replace(/\[|\]/g, '').trim();
                if (optionText) {
                    currentMessage.options.push(optionText);
                }
            }
        });
        return messages;
    };

    // --- State & DOM Elements ---
    const DOM = {
        body: document.body,
        backgroundBlurLayer: document.getElementById('backgroundBlurLayer'),
        mainUI: document.getElementById('mainUI'),
        startScreen: document.getElementById('startScreen'),
        startText: document.getElementById('startText'),
        dialogText: document.getElementById('dialogText'),
        nameText: document.getElementById('nameText'),
        dialogContainer: document.getElementById('dialogContainer'),
        bounceBall: document.getElementById('bounceBall'),
        optionsContainer: document.getElementById('optionsContainer'),
        option1: document.getElementById('option1'),
        option2: document.getElementById('option2'),
        option3: document.getElementById('option3'),
        bgmPlayer: document.getElementById('bgmPlayer'),
        sfxPlayer: document.getElementById('sfxPlayer'),
        transitionOverlay: document.getElementById('transitionOverlay'),
        playButton: document.getElementById('playButton'),
        muteButton: document.getElementById('muteButton'),
        restartButton: document.getElementById('restartButton'),
        endButton: document.getElementById('endButton'),
        eventModal: document.getElementById('eventModal'),
        eventImage: document.getElementById('eventImage'),
        eventContent: document.getElementById('eventContent'),
        errorToast: document.getElementById('errorToast')
    };

    let state = {
        currentMessageIndex: 0,
        currentSegmentIndex: 0,
        currentIndex: 0,
        isTyping: false,
        allMessagesDisplayed: false,
        autoPlay: false,
        userInteracted: false,
        currentBgm: null,
        currentBackgroundUrl: null,
        processedMessages: [],
        messageInfo: [],
        typingTimeoutId: null,
        isMuted: false,
        musicResources: {},
        imgResources: {},
        sfxResources: {},
        messagesAndOptions: [],
        wasAutoPlayBeforeEvent: false
    };

    // --- Initialization ---
    function init() {
        loadResources();
        setupEventListeners();
        adjustFontSize();
        processMessages();
        updateDialogContent();
        executeTransition(() => {
            renderCurrentMessage();
        });
        animateBounceBall();
    }

    function loadResources() {
        state.musicResources = parseResources('musicResData', 'music_res');
        state.imgResources = parseResources('imgResData', 'img_res');
        state.sfxResources = parseResources('sfxResData', 'sfx_res');
        state.messagesAndOptions = parseMessagesAndOptions('messageData', 'data');

        if (Object.keys(state.imgResources).length === 0) {
            showErrorToast("è­¦å‘Š: èƒŒæ™¯å›¾ç‰‡èµ„æºæœªåŠ è½½æˆ–æ ¼å¼é”™è¯¯ã€‚");
        }
        if (Object.keys(state.musicResources).length === 0) {
            showErrorToast("è­¦å‘Š: èƒŒæ™¯éŸ³ä¹èµ„æºæœªåŠ è½½æˆ–æ ¼å¼é”™è¯¯ã€‚");
        }
        if (Object.keys(state.sfxResources).length === 0) {
            showErrorToast("è­¦å‘Š: éŸ³æ•ˆèµ„æºæœªåŠ è½½æˆ–æ ¼å¼é”™è¯¯ã€‚");
        }
        if (state.messagesAndOptions.length === 0) {
            showErrorToast("è­¦å‘Š: å¯¹è¯æ¶ˆæ¯æœªåŠ è½½æˆ–æ ¼å¼é”™è¯¯ã€‚");
        }
    }

    function setupEventListeners() {
        DOM.body.addEventListener('click', onBodyClick);
        DOM.playButton.addEventListener('click', onPlayButtonClick);
        DOM.muteButton.addEventListener('click', onMuteButtonClick);
        DOM.restartButton.addEventListener('click', onRestartButtonClick);
        DOM.endButton.addEventListener('click', onEndButtonClick);
        DOM.option1.addEventListener('click', (e) => { e.stopPropagation(); handleOptionClick(DOM.option1.textContent); });
        DOM.option2.addEventListener('click', (e) => { e.stopPropagation(); handleOptionClick(DOM.option2.textContent); });
        DOM.option3.addEventListener('click', (e) => { e.stopPropagation(); handleOptionClick(DOM.option3.textContent); });
        window.addEventListener('resize', onWindowResize);

        DOM.eventModal.addEventListener('click', hideEventModal);
        document.addEventListener('keydown', (e) => {
            if (e.key === 'Escape' && DOM.eventModal.classList.contains('visible')) {
                hideEventModal();
            }
        });
    }

    function adjustFontSize() {
        const size = Math.max(14, Math.min(22, Math.floor(DOM.dialogContainer.offsetWidth / 20)));
        DOM.dialogText.style.fontSize = `${size}px`;
    }

    // --- Message Processing ---
    function processMessages() {
        state.processedMessages = [];
        state.messageInfo = [];
        state.messagesAndOptions.forEach(item => {
            state.messageInfo.push(item);
            if (item.type === 'dialog') {
                // ä¼˜åŒ–ï¼šå¢åŠ æ¯æ®µæœ€å¤§å­—ç¬¦æ•°ï¼Œé¿å…ä¿¡æ¯ä¸¢å¤±
                const maxChars = 45;
                const content = item.content;
                if (content.length <= maxChars) {
                    state.processedMessages.push([content]);
                } else {
                    let segments = [];
                    for (let i = 0; i < content.length; i += maxChars) {
                        segments.push(content.substring(i, i + maxChars));
                    }
                    state.processedMessages.push(segments);
                }
            } else {
                state.processedMessages.push([item.content]);
            }
        });
    }

    // --- UI Updates ---
    function updateDialogContent() {
        const info = getCurrentInfo();
        if (info.type === 'dialog') {
            DOM.nameText.textContent = info.name || "ç³»ç»Ÿæ¶ˆæ¯";
        }
    }

    function showBlurLayer() {
        DOM.backgroundBlurLayer.classList.add('show-blur');
        DOM.backgroundBlurLayer.classList.remove('event-blur');
    }
    function hideBlurLayer() {
        DOM.backgroundBlurLayer.classList.remove('show-blur');
    }
    function showEventBlur() {
        DOM.backgroundBlurLayer.classList.add('show-blur', 'event-blur');
    }
    function hideEventBlur() {
        DOM.backgroundBlurLayer.classList.remove('event-blur');
    }

    function showMainDialog() {
        DOM.mainUI.style.display = 'block';
        DOM.startScreen.style.display = 'none';
        hideBlurLayer();
        startTypingProcess();
    }

    function showStartScreen() {
        const info = getCurrentInfo();
        DOM.mainUI.style.display = 'none';
        DOM.optionsContainer.style.display = 'none';
        DOM.startScreen.style.display = 'flex';
        showBlurLayer();
        DOM.startText.textContent = info.content;
        DOM.startText.classList.remove('fade-in-effect');
        void DOM.startText.offsetWidth;
        DOM.startText.classList.add('fade-in-effect');
        updateMusic();
        playSfx();
    }

    function renderCurrentMessage() {
        const info = getCurrentInfo();
        if (info.name === 'start') {
            showStartScreen();
        } else {
            showMainDialog();
        }
    }

    function showOptions() {
        if (!state.allMessagesDisplayed) return;
        DOM.bounceBall.style.display = 'none';

        const currentMessageData = state.messageInfo[state.currentMessageIndex];
        const options = currentMessageData && currentMessageData.type === 'dialog' ? currentMessageData.options : [];

        DOM.option1.textContent = '';
        DOM.option2.textContent = '';
        DOM.option3.textContent = '';

        if (options.length > 0) DOM.option1.textContent = options[0];
        if (options.length > 1) DOM.option2.textContent = options[1];
        if (options.length > 2) DOM.option3.textContent = options[2];

        DOM.optionsContainer.style.display = 'block';
        DOM.dialogContainer.style.pointerEvents = 'none';
        DOM.body.style.cursor = 'default';
    }

    function closeDialog() {
        DOM.optionsContainer.style.display = 'none';
        DOM.dialogContainer.style.display = 'flex';
        DOM.body.style.cursor = 'pointer';
        DOM.dialogContainer.style.pointerEvents = 'auto';
        state.allMessagesDisplayed = false;
    }

    // --- Audio ---
    function updateMusic() {
        if (state.isMuted) {
            DOM.bgmPlayer.pause();
            return;
        }
        const info = getCurrentInfo();
        if (info.type === 'dialog' && info.music !== 'æ— ') {
            const musicUrl = state.musicResources[info.music];
            if (musicUrl) {
                if (state.currentBgm !== info.music) {
                    DOM.bgmPlayer.src = musicUrl;
                    state.currentBgm = info.music;
                    if (state.userInteracted) {
                        DOM.bgmPlayer.play().catch(e => console.warn("BGMæ’­æ”¾å¤±è´¥:", e));
                    }
                }
            } else {
                showErrorToast(`è­¦å‘Š: æœªæ‰¾åˆ°éŸ³ä¹èµ„æº "${info.music}"ã€‚`);
            }
        } else {
            if (DOM.bgmPlayer.src) {
                DOM.bgmPlayer.pause();
            }
        }
    }

    // Bugä¿®å¤ï¼šæ›´æ–° playSfx å‡½æ•°ï¼Œç¡®ä¿åœ¨é™éŸ³çŠ¶æ€ä¸‹æ°¸ä¸æ’­æ”¾
    function playSfx() {
        // ä¿®å¤ï¼šå¦‚æœå½“å‰æ˜¯é™éŸ³çŠ¶æ€ï¼Œåˆ™ä¸æ’­æ”¾ä»»ä½•éŸ³æ•ˆ
        if (state.isMuted) {
            return;
        }
        // ä¸å†æ£€æŸ¥ state.isMutedï¼Œå› ä¸ºä¸Šé¢å·²ç»å¤„ç†äº†
        const info = getCurrentInfo();
        if (info.type === 'dialog' && info.sfx !== 'æ— ') {
            const sfxUrl = state.sfxResources[info.sfx];
            if (sfxUrl) {
                // æ€»æ˜¯å°è¯•æ’­æ”¾æˆ–é‡æ–°æ’­æ”¾éŸ³æ•ˆ
                DOM.sfxPlayer.src = sfxUrl;
                DOM.sfxPlayer.currentTime = 0; // é‡ç½®æ’­æ”¾ä½ç½®
                DOM.sfxPlayer.play().catch(e => {
                    console.warn("SFXæ’­æ”¾å¤±è´¥:", e);
                    // å¯ä»¥é€‰æ‹©åœ¨è¿™é‡Œä¸æ˜¾ç¤ºé”™è¯¯ï¼Œå› ä¸ºå¯èƒ½æ˜¯ç”¨æˆ·æœªäº¤äº’
                });
            } else {
                showErrorToast(`è­¦å‘Š: æœªæ‰¾åˆ°éŸ³æ•ˆèµ„æº "${info.sfx}"ã€‚`);
            }
        }
        // å¦‚æœ sfx æ˜¯ 'æ— 'ï¼Œåˆ™ä¸æ‰§è¡Œä»»ä½•æ“ä½œï¼Œè‡ªç„¶ä¸ä¼šæ’­æ”¾
    }

    // æ–°å¢ï¼šåœæ­¢éŸ³æ•ˆçš„å‡½æ•°
    function stopSfx() {
        if (DOM.sfxPlayer.src) {
            DOM.sfxPlayer.pause();
            DOM.sfxPlayer.currentTime = 0; // é‡ç½®æ’­æ”¾ä½ç½®
        }
    }

    // --- Transitions & Animations ---
    function executeTransition(callback) {
        const info = getCurrentInfo();
        let newUrl = null;
        let transitionType = 'none';

        if (info.type === 'dialog') {
            newUrl = state.imgResources[info.background];
            transitionType = info.transition;
        }

        const applyBackground = (onComplete) => {
            if (newUrl && newUrl !== state.currentBackgroundUrl) {
                const imageUrl = `url('${newUrl}')`;
                DOM.body.style.backgroundImage = imageUrl;
                DOM.backgroundBlurLayer.style.backgroundImage = imageUrl;
                state.currentBackgroundUrl = newUrl;
            }
            if (onComplete) onComplete();
        };

        if (transitionType === 'fade' && info.type === 'dialog') {
            DOM.transitionOverlay.style.pointerEvents = 'auto';
            DOM.transitionOverlay.style.opacity = 1;
            DOM.mainUI.style.opacity = 0;
            setTimeout(() => {
                updateDialogContent();
                updateMusic();
                playSfx(); // æ’­æ”¾æ–°æ¶ˆæ¯çš„éŸ³æ•ˆ
                applyBackground();
                if (callback) callback();
                DOM.transitionOverlay.style.opacity = 0;
                DOM.mainUI.style.opacity = 1;
                setTimeout(() => {
                    DOM.transitionOverlay.style.pointerEvents = 'none';
                }, 500);
            }, 500);
        } else {
            updateDialogContent();
            updateMusic();
            playSfx(); // æ’­æ”¾æ–°æ¶ˆæ¯çš„éŸ³æ•ˆ
            applyBackground(() => {
                if (info.type === 'dialog' && transitionType === 'shake') {
                    DOM.body.classList.add('shake');
                    setTimeout(() => {
                        DOM.body.classList.remove('shake');
                        if (callback) callback();
                    }, 300);
                } else {
                    if (callback) callback();
                }
            });
        }
    }

    // æ–°å¢ï¼šä¸ºç‰¹å®šæ¶ˆæ¯ç´¢å¼•æ‰§è¡Œè¿‡æ¸¡çš„å‡½æ•°ï¼Œç”¨äºä¿®å¤é‡å¯æŒ‰é’®çš„bug
    function executeTransitionForMessage(messageIndex) {
        const originalIndex = state.currentMessageIndex;
        state.currentMessageIndex = messageIndex;
        executeTransition(() => {
            renderCurrentMessage();
        });
        state.currentMessageIndex = originalIndex;
    }


    function animateBounceBall() {
        if (state.allMessagesDisplayed || state.isTyping) {
            DOM.bounceBall.style.display = 'none';
            return;
        }
        DOM.bounceBall.style.display = 'block';
        let time = 0;
        const amp = 12;
        const freq = 0.2;
        const bounce = () => {
            if (state.allMessagesDisplayed || state.isTyping) {
                DOM.bounceBall.style.display = 'none';
                return;
            }
            DOM.bounceBall.style.transform = `translateY(${-Math.abs(Math.sin(time * freq)) * amp}px)`;
            time += 0.3;
            requestAnimationFrame(bounce);
        };
        requestAnimationFrame(bounce);
    }

    // --- Typing & Message Flow ---
    function startTypingProcess() {
        if (state.typingTimeoutId !== null) {
            clearTimeout(state.typingTimeoutId);
            state.typingTimeoutId = null;
        }
        state.currentIndex = 0;
        state.isTyping = true;
        typeText();
    }

    function typeText() {
        if (!state.isTyping) return;
        const currentSegments = state.processedMessages[state.currentMessageIndex];
        if (!currentSegments) return;
        const segment = currentSegments[state.currentSegmentIndex];
        if (segment === undefined) return;
        DOM.dialogText.textContent = segment.substring(0, state.currentIndex);
        if (state.currentIndex < segment.length) {
            state.currentIndex++;
            state.typingTimeoutId = setTimeout(typeText, Math.random() * 30 + 20);
        } else {
            state.isTyping = false;
            state.typingTimeoutId = null;

            // ä¼˜åŒ–ï¼šæ£€æŸ¥æ˜¯å¦è¿˜æœ‰æ›´å¤šæ®µè½éœ€è¦æ˜¾ç¤º
            if (state.currentSegmentIndex < currentSegments.length - 1) {
                // è¿˜æœ‰æ›´å¤šæ®µè½ï¼Œå‡†å¤‡æ˜¾ç¤ºä¸‹ä¸€æ®µ
                if (state.autoPlay && !state.allMessagesDisplayed) {
                    setTimeout(() => {
                        state.currentSegmentIndex++;
                        state.currentIndex = 0;
                        state.isTyping = true;
                        typeText();
                    }, 1200);
                }
            } else {
