        stopSfx();

        if (state.isTyping || state.allMessagesDisplayed) return;

        let nextIndex = state.currentMessageIndex + 1;

        while (nextIndex < state.messageInfo.length) {
            const nextInfo = state.messageInfo[nextIndex];

            if (nextInfo.type === 'event') {
                state.currentMessageIndex = nextIndex;
